import { useState } from 'react'
import { X, Send, Bug, CheckCircle } from 'lucide-react'

function BugReportModal({ isOpen, onClose, calculatorName, calculatorPath }) {
    const [formData, setFormData] = useState({
        issueType: 'bug',
        description: '',
        expectedBehavior: '',
        steps: '',
        email: '',
        browser: navigator.userAgent
    })
    const [submitting, setSubmitting] = useState(false)
    const [submitted, setSubmitted] = useState(false)
    const [error, setError] = useState('')

    const handleSubmit = async (e) => {
        e.preventDefault()
        setSubmitting(true)
        setError('')

        try {
            // Using Web3Forms for email - free, no signup for basic use
            const response = await fetch('https://api.web3forms.com/submit', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    access_key: 'f9e0f867-ad62-44d0-8996-2101d22e9281',
                    subject: `­ƒÉø Bug Report: ${calculatorName}`,
                    from_name: 'Plainly Bug Report',
                    calculator: calculatorName,
                    calculator_url: window.location.href,
                    issue_type: formData.issueType,
                    description: formData.description,
                    expected_behavior: formData.expectedBehavior,
                    steps_to_reproduce: formData.steps,
                    reporter_email: formData.email || 'Not provided',
                    browser_info: formData.browser,
                    timestamp: new Date().toISOString()
                })
            })

            if (response.ok) {
                setSubmitted(true)
                setTimeout(() => {
                    onClose()
                    setSubmitted(false)
                    setFormData({
                        issueType: 'bug',
                        description: '',
                        expectedBehavior: '',
                        steps: '',
                        email: '',
                        browser: navigator.userAgent
                    })
                }, 2000)
            } else {
                throw new Error('Failed to submit')
            }
        } catch (err) {
            setError('Failed to submit report. Please try again.')
        } finally {
            setSubmitting(false)
        }
    }

    if (!isOpen) return null

    return (
        <div style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0,0,0,0.8)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999,
            padding: '20px'
        }}>
            <div style={{
                background: '#1a1a1a',
                borderRadius: '16px',
                maxWidth: '500px',
                width: '100%',
                maxHeight: '90vh',
                overflow: 'auto',
                border: '1px solid #333'
            }}>
                {/* Header */}
                <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '20px',
                    borderBottom: '1px solid #333'
                }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                        <Bug size={24} color="#ef4444" />
                        <div>
                            <h2 style={{ margin: 0, fontSize: '18px' }}>Report an Issue</h2>
                            <p style={{ margin: 0, fontSize: '12px', opacity: 0.6 }}>{calculatorName}</p>
                        </div>
                    </div>
                    <button
                        onClick={onClose}
                        style={{
                            background: 'transparent',
                            border: 'none',
                            color: 'white',
                            cursor: 'pointer',
                            padding: '8px'
                        }}
                    >
                        <X size={20} />
                    </button>
                </div>

                {/* Form */}
                {submitted ? (
                    <div style={{
                        padding: '60px 20px',
                        textAlign: 'center'
                    }}>
                        <CheckCircle size={64} color="#22c55e" style={{ marginBottom: '16px' }} />
                        <h3 style={{ margin: 0, color: '#22c55e' }}>Thank you!</h3>
                        <p style={{ opacity: 0.6 }}>Your report has been submitted.</p>
                    </div>
                ) : (
                    <form onSubmit={handleSubmit} style={{ padding: '20px' }}>
                        {/* Issue Type */}
                        <div style={{ marginBottom: '16px' }}>
                            <label style={{
                                display: 'block',
                                marginBottom: '8px',
                                fontSize: '14px',
                                fontWeight: 500
                            }}>
                                Issue Type
                            </label>
                            <select
                                value={formData.issueType}
                                onChange={(e) => setFormData({ ...formData, issueType: e.target.value })}
                                style={{
                                    width: '100%',
                                    padding: '12px',
                                    borderRadius: '8px',
                                    border: '1px solid #333',
                                    background: '#0a0a0a',
                                    color: 'white',
                                    fontSize: '14px'
                                }}
                            >
                                <option value="bug">­ƒÉø Bug / Error</option>
                                <option value="calculation">­ƒöó Wrong Calculation</option>
                                <option value="ui">­ƒÄ¿ UI/Design Issue</option>
                                <option value="performance">ÔÜí Performance Issue</option>
                                <option value="feature">­ƒÆí Feature Request</option>
                                <option value="other">­ƒôØ Other</option>
                            </select>
                        </div>

                        {/* Description */}
                        <div style={{ marginBottom: '16px' }}>
                            <label style={{
                                display: 'block',
                                marginBottom: '8px',
                                fontSize: '14px',
                                fontWeight: 500
                            }}>
                                Describe the Issue *
                            </label>
                            <textarea
                                required
                                value={formData.description}
                                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                placeholder="What went wrong? Be as specific as possible..."
                                rows={4}
                                style={{
                                    width: '100%',
                                    padding: '12px',
                                    borderRadius: '8px',
                                    border: '1px solid #333',
                                    background: '#0a0a0a',
                                    color: 'white',
                                    fontSize: '14px',
                                    resize: 'vertical'
                                }}
                            />
                        </div>

                        {/* Expected Behavior */}
                        <div style={{ marginBottom: '16px' }}>
                            <label style={{
                                display: 'block',
                                marginBottom: '8px',
                                fontSize: '14px',
                                fontWeight: 500
                            }}>
                                Expected Behavior
                            </label>
                            <textarea
                                value={formData.expectedBehavior}
                                onChange={(e) => setFormData({ ...formData, expectedBehavior: e.target.value })}
                                placeholder="What did you expect to happen?"
                                rows={2}
                                style={{
                                    width: '100%',
                                    padding: '12px',
                                    borderRadius: '8px',
                                    border: '1px solid #333',
                                    background: '#0a0a0a',
                                    color: 'white',
                                    fontSize: '14px',
                                    resize: 'vertical'
                                }}
                            />
                        </div>

                        {/* Steps to Reproduce */}
                        <div style={{ marginBottom: '16px' }}>
                            <label style={{
                                display: 'block',
                                marginBottom: '8px',
                                fontSize: '14px',
                                fontWeight: 500
                            }}>
                                Steps to Reproduce
                            </label>
                            <textarea
                                value={formData.steps}
                                onChange={(e) => setFormData({ ...formData, steps: e.target.value })}
                                placeholder="1. Enter value X&#10;2. Click button Y&#10;3. See error..."
                                rows={3}
                                style={{
                                    width: '100%',
                                    padding: '12px',
                                    borderRadius: '8px',
                                    border: '1px solid #333',
                                    background: '#0a0a0a',
                                    color: 'white',
                                    fontSize: '14px',
                                    resize: 'vertical'
                                }}
                            />
                        </div>

                        {/* Email */}
                        <div style={{ marginBottom: '16px' }}>
                            <label style={{
                                display: 'block',
                                marginBottom: '8px',
                                fontSize: '14px',
                                fontWeight: 500
                            }}>
                                Your Email (optional)
                            </label>
                            <input
                                type="email"
                                value={formData.email}
                                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                placeholder="For follow-up questions"
                                style={{
                                    width: '100%',
                                    padding: '12px',
                                    borderRadius: '8px',
                                    border: '1px solid #333',
                                    background: '#0a0a0a',
                                    color: 'white',
                                    fontSize: '14px'
                                }}
                            />
                        </div>

                        {/* Browser Info */}
                        <div style={{
                            marginBottom: '20px',
                            padding: '12px',
                            background: '#0a0a0a',
                            borderRadius: '8px',
                            fontSize: '11px',
                            opacity: 0.5
                        }}>
                            ­ƒô▒ Browser info will be included automatically
                        </div>

                        {error && (
                            <div style={{
                                padding: '12px',
                                background: '#ef444420',
                                borderRadius: '8px',
                                color: '#ef4444',
                                marginBottom: '16px',
                                fontSize: '14px'
                            }}>
                                {error}
                            </div>
                        )}

                        {/* Submit Button */}
                        <button
                            type="submit"
                            disabled={submitting}
                            style={{
                                width: '100%',
                                padding: '14px',
                                background: submitting ? '#333' : 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                                border: 'none',
                                borderRadius: '8px',
                                color: 'white',
                                fontSize: '16px',
                                fontWeight: 600,
                                cursor: submitting ? 'wait' : 'pointer',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                gap: '8px'
                            }}
                        >
                            <Send size={18} />
                            {submitting ? 'Submitting...' : 'Submit Report'}
                        </button>
                    </form>
                )}
            </div>
        </div>
    )
}

export default BugReportModal

import { useState } from 'react'
import { Link, useNavigate, useLocation } from 'react-router-dom'
import { ChevronLeft, Share2, Star, RotateCcw, Bug } from 'lucide-react'
import { useStorage } from '../../context/StorageContext'
import BugReportModal from '../BugReportModal'
import './CalculatorLayout.css'

function CalculatorLayout({
    title,
    description,
    category,
    categoryPath,
    icon: Icon,
    children,
    result,
    resultLabel,
    resultUnit,
    resultDetails,
    onReset
}) {
    const navigate = useNavigate()
    const location = useLocation()
    const { isFavorite, toggleFavorite, addToHistory } = useStorage()
    const [showBugReport, setShowBugReport] = useState(false)

    const currentPath = location.pathname
    const isCurrentFavorite = isFavorite(currentPath)

    const handleFavoriteClick = () => {
        toggleFavorite(currentPath)
    }

    const handleShare = async () => {
        const shareUrl = window.location.href
        if (navigator.share) {
            try {
                await navigator.share({
                    title: title,
                    text: description,
                    url: shareUrl,
                })
            } catch (err) {
                // User cancelled or share failed
            }
        } else {
            // Fallback: copy to clipboard
            try {
                await navigator.clipboard.writeText(shareUrl)
                // Could show a toast notification here
            } catch (err) {
                console.error('Failed to copy:', err)
            }
        }
    }

    return (
        <div className="calculator-page">
            <div className="container">
                {/* Breadcrumb */}
                <div className="calc-breadcrumb">
                    <button className="back-btn" onClick={() => navigate(-1)}>
                        <ChevronLeft size={16} />
                    </button>
                    <Link to={categoryPath || '/'} className="breadcrumb-link">
                        {category}
                    </Link>
                </div>

                {/* Header */}
                <header className="calc-header">
                    <div className="calc-header-main">
                        {Icon && (
                            <div className="calc-icon">
                                <Icon size={24} />
                            </div>
                        )}
                        <div className="calc-header-text">
                            <h1 className="calc-title">{title}</h1>
                            <p className="calc-description">{description}</p>
                        </div>
                    </div>
                    <div className="calc-actions">
                        <button className="action-btn" onClick={handleShare} aria-label="Share">
                            <Share2 size={18} />
                        </button>
                        <button
                            className={`action-btn ${isCurrentFavorite ? 'active' : ''}`}
                            onClick={handleFavoriteClick}
                            aria-label={isCurrentFavorite ? 'Remove from favorites' : 'Add to favorites'}
                        >
                            <Star size={18} fill={isCurrentFavorite ? 'currentColor' : 'none'} />
                        </button>
                        <button
                            className="action-btn"
                            onClick={onReset || (() => window.location.reload())}
                            aria-label="Reset"
                            title="Reset values"
                        >
                            <RotateCcw size={18} />
                        </button>
                        <button
                            className="action-btn bug-report-btn"
                            onClick={() => setShowBugReport(true)}
                            aria-label="Report Bug"
                            title="Report an issue"
                        >
                            <Bug size={18} />
                        </button>
                    </div>
                </header>

                {/* Bug Report Modal */}
                <BugReportModal
                    isOpen={showBugReport}
                    onClose={() => setShowBugReport(false)}
                    calculatorName={title}
                    calculatorPath={currentPath}
                />


                {/* Main Content Grid */}
                <div className="calc-grid">
                    {/* Left: Parameters */}
                    <div className="calc-parameters">
                        <h2 className="section-label">Parameters</h2>
                        <div className="parameters-content">
                            {children}
                        </div>
                    </div>

                    {/* Right: Result */}
                    <div className="calc-result-section">
                        <h2 className="section-label">{resultLabel || 'Result'}</h2>
                        <div className="result-card">
                            <div className="result-display">
                                <span className="result-value">{result || '0'}</span>
                                {resultUnit && <span className="result-unit">{resultUnit}</span>}
                            </div>
                        </div>

                        {/* Additional Result Details */}
                        {resultDetails && (
                            <div className="result-details">
                                {resultDetails}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default CalculatorLayout

import { useState, useEffect, useRef } from 'react'
import { Link, NavLink, useLocation, useNavigate } from 'react-router-dom'
import { Search, Menu, X, Settings, ArrowRight, ChevronDown, Coffee } from 'lucide-react'
import { allCalculators } from '../../data/calculators'
import { useSettings } from '../../context/SettingsContext'
import './Header.css'

function Header() {
    const [isScrolled, setIsScrolled] = useState(false)
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)
    const [searchQuery, setSearchQuery] = useState('')
    const [showDropdown, setShowDropdown] = useState(false)
    const [showCategoryDropdown, setShowCategoryDropdown] = useState(false)
    const [mobileSearchQuery, setMobileSearchQuery] = useState('')
    const searchRef = useRef(null)
    const categoryRef = useRef(null)
    const mobileSearchRef = useRef(null)
    const location = useLocation()
    const navigate = useNavigate()
    const { openSettings } = useSettings()

    const categories = [
        { name: 'AI Tools', path: '/ai' },
        { name: 'Finance', path: '/finance' },
        { name: 'Health', path: '/health' },
        { name: 'Math', path: '/math' },
        { name: 'Text', path: '/calculators?category=Text' },
        { name: 'Tech', path: '/calculators?category=Tech' },
        { name: 'Sustainability', path: '/calculators?category=Sustainability' },
        { name: 'Real Estate', path: '/calculators?category=Real%20Estate' },
        { name: 'Fun', path: '/calculators?category=Fun' },
        { name: 'Converter', path: '/converter' },
        { name: 'Other', path: '/calculators?category=Other' },
    ]

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 10)
        }
        window.addEventListener('scroll', handleScroll)
        return () => window.removeEventListener('scroll', handleScroll)
    }, [])

    useEffect(() => {
        setIsMobileMenuOpen(false)
        setShowDropdown(false)
        setShowCategoryDropdown(false)
        setSearchQuery('')
        setMobileSearchQuery('')
    }, [location])

    // Close dropdowns when clicking outside
    useEffect(() => {
        const handleClickOutside = (e) => {
            if (searchRef.current && !searchRef.current.contains(e.target)) {
                setShowDropdown(false)
            }
            if (categoryRef.current && !categoryRef.current.contains(e.target)) {
                setShowCategoryDropdown(false)
            }
        }
        document.addEventListener('mousedown', handleClickOutside)
        return () => document.removeEventListener('mousedown', handleClickOutside)
    }, [])

    // Lock body scroll when mobile menu is open
    useEffect(() => {
        if (isMobileMenuOpen) {
            document.body.style.overflow = 'hidden'
        } else {
            document.body.style.overflow = ''
        }
        return () => {
            document.body.style.overflow = ''
        }
    }, [isMobileMenuOpen])

    // Filter calculators based on search query
    const filteredCalculators = searchQuery.trim()
        ? allCalculators.filter(calc =>
            calc.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            calc.description.toLowerCase().includes(searchQuery.toLowerCase())
        ).slice(0, 5)
        : []

    // Filter for mobile search
    const mobileFilteredCalculators = mobileSearchQuery.trim()
        ? allCalculators.filter(calc =>
            calc.name.toLowerCase().includes(mobileSearchQuery.toLowerCase()) ||
            calc.description.toLowerCase().includes(mobileSearchQuery.toLowerCase())
        ).slice(0, 5)
        : []

    const handleSearch = (e) => {
        e.preventDefault()
        if (searchQuery.trim()) {
            navigate(`/calculators?search=${encodeURIComponent(searchQuery.trim())}`)
            setSearchQuery('')
            setShowDropdown(false)
        }
    }

    const handleMobileSearch = (e) => {
        e.preventDefault()
        if (mobileSearchQuery.trim()) {
            navigate(`/calculators?search=${encodeURIComponent(mobileSearchQuery.trim())}`)
            setMobileSearchQuery('')
            setIsMobileMenuOpen(false)
        }
    }

    const handleInputChange = (e) => {
        setSearchQuery(e.target.value)
        setShowDropdown(e.target.value.trim().length > 0)
    }

    const handleMobileInputChange = (e) => {
        setMobileSearchQuery(e.target.value)
    }

    const handleResultClick = () => {
        setShowDropdown(false)
        setSearchQuery('')
    }

    const handleMobileResultClick = () => {
        setMobileSearchQuery('')
        setIsMobileMenuOpen(false)
    }

    const navLinks = [
        { name: 'FINANCE', path: '/finance' },
        { name: 'HEALTH', path: '/health' },
        { name: 'MATH', path: '/math' },
    ]

    return (
        <>
            <header className={`header ${isScrolled ? 'header-scrolled' : ''}`}>
                <div className="container header-container">
                    {/* Logo */}
                    <Link to="/" className="logo">
                        <span className="logo-icon">Ôùç</span>
                        <span className="logo-text">PLAINLY</span>
                    </Link>

                    {/* Desktop Navigation */}
                    <nav className="nav-desktop">
                        <NavLink to="/calculators" className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}>
                            ALL TOOLS
                        </NavLink>
                        {navLinks.map((link) => (
                            <NavLink
                                key={link.path}
                                to={link.path}
                                className={({ isActive }) => `nav-link ${isActive ? 'active' : ''}`}
                            >
                                {link.name}
                            </NavLink>
                        ))}
                        {/* Categories Dropdown */}
                        <div className="nav-dropdown-container" ref={categoryRef}>
                            <button
                                className={`nav-link nav-dropdown-toggle ${showCategoryDropdown ? 'active' : ''}`}
                                onClick={() => setShowCategoryDropdown(!showCategoryDropdown)}
                            >
                                CATEGORIES
                                <ChevronDown size={14} className={`nav-dropdown-icon ${showCategoryDropdown ? 'rotate' : ''}`} />
                            </button>
                            {showCategoryDropdown && (
                                <div className="nav-dropdown-menu">
                                    {categories.map((cat) => (
                                        <Link
                                            key={cat.name}
                                            to={cat.path}
                                            className="nav-dropdown-item"
                                            onClick={() => setShowCategoryDropdown(false)}
                                        >
                                            {cat.name}
                                        </Link>
                                    ))}
                                </div>
                            )}
                        </div>
                    </nav>

                    {/* Right Actions */}
                    <div className="header-actions">
                        <div className="header-search-container" ref={searchRef}>
                            <form className="search-bar" onSubmit={handleSearch}>
                                <Search size={16} className="search-bar-icon" />
                                <input
                                    type="text"
                                    placeholder="Search instruments..."
                                    value={searchQuery}
                                    onChange={handleInputChange}
                                    onFocus={() => searchQuery.trim() && setShowDropdown(true)}
                                    className="search-bar-input"
                                />
                            </form>

                            {/* Search Dropdown */}
                            {showDropdown && filteredCalculators.length > 0 && (
                                <div className="header-search-dropdown">
                                    {filteredCalculators.map((calc) => (
                                        <Link
                                            to={calc.path}
                                            key={calc.path}
                                            className="header-search-result"
                                            onClick={handleResultClick}
                                        >
                                            <div className="header-search-result-icon">
                                                <calc.icon size={14} />
                                            </div>
                                            <div className="header-search-result-info">
                                                <span className="header-search-result-name">{calc.name}</span>
                                                <span className="header-search-result-category">{calc.category}</span>
                                            </div>
                                            <ArrowRight size={12} className="header-search-result-arrow" />
                                        </Link>
                                    ))}
                                    <Link
                                        to={`/calculators?search=${encodeURIComponent(searchQuery)}`}
                                        className="header-search-view-all"
                                        onClick={handleResultClick}
                                    >
                                        View all results ÔåÆ
                                    </Link>
                                </div>
                            )}
                        </div>

                        <button className="header-icon-btn" aria-label="Settings" onClick={openSettings}>
                            <Settings size={18} />
                        </button>

                        <a
                            href="https://buymeacoffee.com/plainlyy"
                            target="_blank"
                            rel="noopener noreferrer"
                            className="header-bmc-link"
                        >
                            <Coffee size={16} />
                            <span>Buy me a coffee</span>
                        </a>

                        {/* Mobile Menu Toggle */}
                        <button
                            className="mobile-menu-btn"
                            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                            aria-label="Toggle menu"
                        >
                            {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
                        </button>
                    </div>
                </div>
            </header>

            {/* Mobile Menu - Outside header to fix backdrop-filter issue */}
            <div className={`mobile-menu ${isMobileMenuOpen ? 'open' : ''}`}>
                {/* Mobile Search */}
                <div className="mobile-search" ref={mobileSearchRef}>
                    <form onSubmit={handleMobileSearch}>
                        <div className="mobile-search-wrapper">
                            <Search size={18} className="mobile-search-icon" />
                            <input
                                type="text"
                                placeholder="Search calculators..."
                                value={mobileSearchQuery}
                                onChange={handleMobileInputChange}
                                className="mobile-search-input"
                            />
                        </div>
                    </form>

                    {/* Mobile Search Results */}
                    {mobileSearchQuery.trim() && mobileFilteredCalculators.length > 0 && (
                        <div className="mobile-search-results">
                            {mobileFilteredCalculators.map((calc) => (
                                <Link
                                    to={calc.path}
                                    key={calc.path}
                                    className="mobile-search-result"
                                    onClick={handleMobileResultClick}
                                >
                                    <div className="mobile-search-result-icon">
                                        <calc.icon size={18} />
                                    </div>
                                    <div className="mobile-search-result-info">
                                        <span className="mobile-search-result-name">{calc.name}</span>
                                        <span className="mobile-search-result-category">{calc.category}</span>
                                    </div>
                                    <ArrowRight size={16} />
                                </Link>
                            ))}
                        </div>
                    )}
                </div>

                <nav className="mobile-nav">
                    {navLinks.map((link) => (
                        <NavLink
                            key={link.path}
                            to={link.path}
                            className={({ isActive }) => `mobile-nav-link ${isActive ? 'active' : ''}`}
                        >
                            {link.name}
                        </NavLink>
                    ))}
                    <div className="mobile-nav-divider" />
                    <NavLink to="/calculators" className="mobile-nav-link">ALL CALCULATORS</NavLink>
                    <a
                        href="https://buymeacoffee.com/plainlyy"
                        target="_blank"
                        rel="noopener noreferrer"
                        className="mobile-nav-link bmc-link"
                    >
                        <Coffee size={18} />
                        SUPPORT US
