import { createContext, useContext, useState, useEffect } from 'react'

const SettingsContext = createContext(null)

const STORAGE_KEY = 'plainly_settings'

const DEFAULT_SETTINGS = {
    theme: 'dark',       // dark, light, sepia, rose
    decimalPlaces: 2,    // 2, 4, 6
    currency: 'USD',     // USD, EUR, GBP, INR
    unitSystem: 'metric' // metric, imperial
}

export function SettingsProvider({ children }) {
    const [settings, setSettings] = useState(DEFAULT_SETTINGS)
    const [isSettingsOpen, setIsSettingsOpen] = useState(false)

    // Load settings from localStorage on mount
    useEffect(() => {
        try {
            const saved = localStorage.getItem(STORAGE_KEY)
            if (saved) {
                setSettings({ ...DEFAULT_SETTINGS, ...JSON.parse(saved) })
            }
        } catch (error) {
            console.error('Error loading settings:', error)
        }
    }, [])

    // Apply theme to document
    useEffect(() => {
        document.documentElement.setAttribute('data-theme', settings.theme)
    }, [settings.theme])

    // Save settings to localStorage whenever they change
    useEffect(() => {
        try {
            localStorage.setItem(STORAGE_KEY, JSON.stringify(settings))
        } catch (error) {
            console.error('Error saving settings:', error)
        }
    }, [settings])

    const updateSetting = (key, value) => {
        setSettings(prev => ({ ...prev, [key]: value }))
    }

    const resetSettings = () => {
        setSettings(DEFAULT_SETTINGS)
    }

    const openSettings = () => setIsSettingsOpen(true)
    const closeSettings = () => setIsSettingsOpen(false)

    const value = {
        settings,
        updateSetting,
        resetSettings,
        isSettingsOpen,
        openSettings,
        closeSettings
    }

    return (
        <SettingsContext.Provider value={value}>
            {children}
        </SettingsContext.Provider>
    )
}

export function useSettings() {
    const context = useContext(SettingsContext)
    if (!context) {
        throw new Error('useSettings must be used within a SettingsProvider')
    }
    return context
}

export default SettingsContext

import { createContext, useContext, useState, useEffect } from 'react'

const StorageContext = createContext(null)

const STORAGE_KEYS = {
    FAVORITES: 'plainly_favorites',
    HISTORY: 'plainly_history',
}

const MAX_HISTORY_ITEMS = 10

export function StorageProvider({ children }) {
    const [favorites, setFavorites] = useState([])
    const [history, setHistory] = useState([])

    // Load from localStorage on mount
    useEffect(() => {
        try {
            const savedFavorites = localStorage.getItem(STORAGE_KEYS.FAVORITES)
            const savedHistory = localStorage.getItem(STORAGE_KEYS.HISTORY)

            if (savedFavorites) {
                setFavorites(JSON.parse(savedFavorites))
            }
            if (savedHistory) {
                setHistory(JSON.parse(savedHistory))
            }
        } catch (error) {
            console.error('Error loading from localStorage:', error)
        }
    }, [])

    // Save favorites to localStorage
    useEffect(() => {
        try {
            localStorage.setItem(STORAGE_KEYS.FAVORITES, JSON.stringify(favorites))
        } catch (error) {
            console.error('Error saving favorites:', error)
        }
    }, [favorites])

    // Save history to localStorage
    useEffect(() => {
        try {
            localStorage.setItem(STORAGE_KEYS.HISTORY, JSON.stringify(history))
        } catch (error) {
            console.error('Error saving history:', error)
        }
    }, [history])

    const toggleFavorite = (calculatorPath) => {
        setFavorites(prev => {
            if (prev.includes(calculatorPath)) {
                return prev.filter(path => path !== calculatorPath)
            } else {
                return [...prev, calculatorPath]
            }
        })
    }

    const isFavorite = (calculatorPath) => {
        return favorites.includes(calculatorPath)
    }

    const addToHistory = (entry) => {
        // entry: { path, name, result, timestamp }
        setHistory(prev => {
            // Remove duplicate if exists
            const filtered = prev.filter(item => item.path !== entry.path)
            // Add new entry at the beginning
            const updated = [{ ...entry, timestamp: Date.now() }, ...filtered]
            // Keep only MAX_HISTORY_ITEMS
            return updated.slice(0, MAX_HISTORY_ITEMS)
        })
    }

    const clearHistory = () => {
        setHistory([])
    }

    const value = {
        favorites,
        history,
        toggleFavorite,
        isFavorite,
        addToHistory,
        clearHistory,
    }

    return (
        <StorageContext.Provider value={value}>
            {children}
        </StorageContext.Provider>
    )
}

export function useStorage() {
    const context = useContext(StorageContext)
    if (!context) {
        throw new Error('useStorage must be used within a StorageProvider')
    }
    return context
}

export default StorageContext

import { useState, useEffect } from 'react'
import { Link, useSearchParams } from 'react-router-dom'
import { Search, ArrowRight } from 'lucide-react'
import { allCalculators } from '../data/calculators'
import './AllCalculators.css'

const categories = ['All', 'Finance', 'Health', 'Math', 'Text', 'Tech', 'Sustainability', 'Real Estate', 'Fun', 'Converter', 'Other']

function AllCalculators() {
    const [searchParams, setSearchParams] = useSearchParams()
    const [searchQuery, setSearchQuery] = useState(searchParams.get('search') || '')
    const [activeCategory, setActiveCategory] = useState(searchParams.get('category') || 'All')

    // Update search query and category when URL params change
    useEffect(() => {
        const urlSearch = searchParams.get('search') || ''
        const urlCategory = searchParams.get('category') || 'All'
        setSearchQuery(urlSearch)
        setActiveCategory(urlCategory)
    }, [searchParams])

    // Update URL when search query changes
    const handleSearchChange = (e) => {
        const value = e.target.value
        setSearchQuery(value)
        const newParams = {}
        if (value.trim()) newParams.search = value
        if (activeCategory !== 'All') newParams.category = activeCategory
        setSearchParams(newParams)
    }

    // Update URL when category changes
    const handleCategoryChange = (cat) => {
        setActiveCategory(cat)
        const newParams = {}
        if (searchQuery.trim()) newParams.search = searchQuery
        if (cat !== 'All') newParams.category = cat
        setSearchParams(newParams)
    }

    const filteredCalculators = allCalculators.filter(calc => {
        const matchesSearch = calc.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            calc.description.toLowerCase().includes(searchQuery.toLowerCase())
        const matchesCategory = activeCategory === 'All' || calc.category === activeCategory
        return matchesSearch && matchesCategory
    })

    return (
        <div className="all-calculators">
            <div className="container">
                {/* Filters */}
                <div className="filters-bar">
                    <div className="search-wrapper">
                        <Search size={18} className="search-icon" />
                        <input
                            type="text"
                            placeholder="Search calculators..."
                            value={searchQuery}
                            onChange={handleSearchChange}
                            className="search-input"
                        />
                    </div>
                    <div className="category-filters">
                        {categories.map(cat => (
                            <button
                                key={cat}
                                className={`filter-btn ${activeCategory === cat ? 'active' : ''}`}
                                onClick={() => handleCategoryChange(cat)}
                            >
                                {cat}
                            </button>
                        ))}
                    </div>
                </div>

                {/* Results Count */}
                <div className="results-info">
                    <span className="results-count">{filteredCalculators.length} Results</span>
                </div>

                {/* Calculator Grid */}
                <div className="calculators-grid">
                    {filteredCalculators.map((calc) => (
                        <Link to={calc.path} key={calc.path} className="calc-card">
                            <div className="calc-card-icon">
                                <calc.icon size={20} />
                            </div>
                            <h3 className="calc-card-title">{calc.name.replace(' Calculator', '')}</h3>
                            <p className="calc-card-description">{calc.description}</p>
                            <div className="calc-card-footer">
                                <span className="calc-card-complexity">STANDARD</span>
                                <span className="calc-card-action">
                                    LAUNCH <ArrowRight size={14} />
                                </span>
                            </div>
                        </Link>
                    ))}
                </div>

                {/* No Results */}
                {filteredCalculators.length === 0 && (
                    <div className="no-results">
                        <p>No calculators found matching your criteria.</p>
                    </div>
                )}
            </div>
        </div>
    )
}

