            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(2, 1fr)',
                gap: '12px',
                marginBottom: '24px'
            }}>
                <div style={{
                    background: 'linear-gradient(135deg, #f7931a 0%, #f59e0b 100%)',
                    borderRadius: '12px',
                    padding: '16px',
                    textAlign: 'center'
                }}>
                    <div style={{ fontSize: '12px', opacity: 0.9, marginBottom: '4px' }}>­ƒÆ░ Total Value</div>
                    <div style={{ fontSize: '20px', fontWeight: '700' }}>{formatCurrency(results.totalValue)}</div>
                </div>

                <div style={{
                    background: results.totalProfit >= 0
                        ? 'linear-gradient(135deg, #10b981 0%, #059669 100%)'
                        : 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                    borderRadius: '12px',
                    padding: '16px',
                    textAlign: 'center'
                }}>
                    <div style={{ fontSize: '12px', opacity: 0.9, marginBottom: '4px' }}>
                        {results.totalProfit >= 0 ? '­ƒôê' : '­ƒôë'} P/L
                    </div>
                    <div style={{ fontSize: '20px', fontWeight: '700' }}>
                        {formatCurrency(results.totalProfit)} ({results.totalProfitPercent.toFixed(1)}%)
                    </div>
                </div>
            </div>

            {/* Holdings List */}
            <div style={{ marginBottom: '16px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '12px' }}>
                    <h3 style={{ fontSize: '14px', fontWeight: '600' }}>Your Holdings</h3>
                    <button
                        onClick={addHolding}
                        style={{
                            padding: '8px 16px',
                            background: '#8b5cf6',
                            border: 'none',
                            borderRadius: '6px',
                            color: 'white',
                            fontSize: '12px',
                            cursor: 'pointer'
                        }}
                    >
                        + Add Coin
                    </button>
                </div>

                {results.holdings.map((holding, index) => (
                    <div key={index} style={{
                        background: '#1a1a2e',
                        borderRadius: '12px',
                        padding: '16px',
                        marginBottom: '12px',
                        border: '1px solid #333'
                    }}>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '12px' }}>
