function GiftTaxCalculator() {
    const [giftAmount, setGiftAmount] = useState(50000)
    const [numberOfRecipients, setNumberOfRecipients] = useState(1)
    const [giftFromCouple, setGiftFromCouple] = useState(false)
    const [year, setYear] = useState('2024')
    const [previousGifts, setPreviousGifts] = useState(0)

    // Annual exclusion amounts by year
    const annualExclusions = {
        '2024': 18000,
        '2025': 19000
    }

    // Lifetime exemption
    const lifetimeExemptions = {
        '2024': 13610000,
        '2025': 13990000
    }

    const results = useMemo(() => {
        const annualExclusion = annualExclusions[year]
        const lifetimeExemption = lifetimeExemptions[year]

        // If married and gift-splitting
        const effectiveExclusion = giftFromCouple ? annualExclusion * 2 : annualExclusion
        const effectiveLifetimeExemption = giftFromCouple ? lifetimeExemption * 2 : lifetimeExemption

        // Amount per recipient
        const perRecipient = giftAmount / numberOfRecipients

        // Taxable portion per recipient
        const taxablePerRecipient = Math.max(perRecipient - effectiveExclusion, 0)
        const totalTaxableGift = taxablePerRecipient * numberOfRecipients

        // Cumulative taxable gifts
        const cumulativeTaxable = previousGifts + totalTaxableGift

        // Is tax due? (only if lifetime exemption exceeded)
        const taxDue = cumulativeTaxable > effectiveLifetimeExemption

        // Gift tax (40% top rate - simplified)
        const taxableOverExemption = Math.max(cumulativeTaxable - effectiveLifetimeExemption, 0)
        let giftTax = 0
        if (taxableOverExemption > 0) {
            giftTax = taxableOverExemption * 0.40 // Simplified top rate
        }

        // Remaining lifetime exemption
        const remainingExemption = Math.max(effectiveLifetimeExemption - cumulativeTaxable, 0)

        // Amount within exclusion
        const excludedAmount = Math.min(giftAmount, effectiveExclusion * numberOfRecipients)

        return {
            annualExclusion,
            effectiveExclusion,
            effectiveLifetimeExemption,
            perRecipient,
            taxablePerRecipient,
            totalTaxableGift,
            cumulativeTaxable,
            giftTax,
            taxDue,
            remainingExemption,
            excludedAmount,
            requiresForm709: totalTaxableGift > 0
        }
    }, [giftAmount, numberOfRecipients, giftFromCouple, year, previousGifts])

    const formatCurrency = (value) => {
        return new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
            minimumFractionDigits: 0,
            maximumFractionDigits: 0
        }).format(value)
    }

    const handleReset = () => {
        setGiftAmount(50000)
        setNumberOfRecipients(1)
        setGiftFromCouple(false)
        setYear('2024')
        setPreviousGifts(0)
    }

    return (
        <CalculatorLayout
            title="Gift Tax Calculator"
            description="Calculate gift tax and lifetime exemption usage"
            category="Finance"
            categoryPath="/finance"
            icon={Gift}
            result={formatCurrency(results.giftTax)}
            resultLabel="Gift Tax Due"
            onReset={handleReset}
        >
            {/* Result Cards */}
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(2, 1fr)',
                gap: '12px',
                marginBottom: '24px'
            }}>
                <div style={{
                    background: results.taxDue
                        ? 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)'
                        : 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                    borderRadius: '12px',
                    padding: '16px',
                    textAlign: 'center'
                }}>
                    <div style={{ fontSize: '12px', opacity: 0.9, marginBottom: '4px' }}>
                        {results.taxDue ? '­ƒÆ© Tax Due' : 'Ô£à No Tax'}
                    </div>
                    <div style={{ fontSize: '22px', fontWeight: '700' }}>{formatCurrency(results.giftTax)}</div>
                </div>

                <div style={{
                    background: '#1a1a2e',
                    borderRadius: '12px',
                    padding: '16px',
                    textAlign: 'center',
                    border: '1px solid #333'
                }}>
                    <div style={{ fontSize: '12px', opacity: 0.6, marginBottom: '4px' }}>Taxable Gift</div>
                    <div style={{ fontSize: '22px', fontWeight: '600' }}>{formatCurrency(results.totalTaxableGift)}</div>
                </div>
            </div>

            {/* Remaining Exemption */}
            <div style={{
                background: 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)',
                borderRadius: '12px',
                padding: '16px',
                marginBottom: '20px',
                textAlign: 'center'
            }}>
                <div style={{ fontSize: '13px', opacity: 0.9, marginBottom: '4px' }}>
                    ­ƒÆÄ Remaining Lifetime Exemption
                </div>
                <div style={{ fontSize: '24px', fontWeight: '700' }}>
                    {formatCurrency(results.remainingExemption)}
                </div>
            </div>

            {/* Form 709 Notice */}
            {results.requiresForm709 && (
                <div style={{
                    background: 'rgba(245, 158, 11, 0.1)',
                    borderRadius: '10px',
                    padding: '14px',
                    marginBottom: '20px',
                    border: '1px solid rgba(245, 158, 11, 0.2)'
                }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                        <span style={{ fontSize: '18px' }}>­ƒôï</span>
                        <span style={{ fontSize: '13px', fontWeight: '600' }}>Form 709 Required</span>
                    </div>
                    <p style={{ fontSize: '12px', opacity: 0.8, margin: '8px 0 0 0' }}>
                        Gifts exceeding the annual exclusion must be reported on IRS Form 709.
                    </p>
                </div>
            )}

            {/* Inputs */}
            <div className="input-group">
                <label className="input-label">Tax Year</label>
                <select
                    value={year}
                    onChange={(e) => setYear(e.target.value)}
                    style={{
                        width: '100%',
                        padding: '14px',
                        background: '#1a1a2e',
                        border: '1px solid #333',
                        borderRadius: '8px',
                        color: 'white',
                        fontSize: '16px'
                    }}
                >
                    <option value="2024">2024 (Annual: $18K, Lifetime: $13.61M)</option>
                    <option value="2025">2025 (Annual: $19K, Lifetime: $13.99M)</option>
                </select>
            </div>

            <div className="input-group">
                <label className="input-label">
                    <Gift size={14} style={{ marginRight: '6px' }} />
                    Total Gift Amount
                </label>
                <input
                    type="number"
                    value={giftAmount}
                    onChange={(e) => setGiftAmount(Number(e.target.value))}
                    min="0"
                    step="1000"
                    style={{
                        width: '100%',
                        padding: '14px',
                        background: '#1a1a2e',
                        border: '1px solid #333',
                        borderRadius: '8px',
                        color: 'white',
                        fontSize: '16px'
                    }}
                />
            </div>

            <div className="input-group">
                <label className="input-label">
                    <Users size={14} style={{ marginRight: '6px' }} />
                    Number of Recipients
                </label>
                <input
                    type="number"
                    value={numberOfRecipients}
                    onChange={(e) => setNumberOfRecipients(Math.max(1, Number(e.target.value)))}
                    min="1"
                    style={{
                        width: '100%',
                        padding: '14px',
                        background: '#1a1a2e',
                        border: '1px solid #333',
                        borderRadius: '8px',
                        color: 'white',
                        fontSize: '16px'
                    }}
                />
            </div>

            <div className="input-group">
                <label style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '12px',
                    padding: '14px',
                    background: '#1a1a2e',
                    borderRadius: '8px',
                    border: '1px solid #333',
                    cursor: 'pointer'
                }}>
                    <input
                        type="checkbox"
                        checked={giftFromCouple}
                        onChange={(e) => setGiftFromCouple(e.target.checked)}
                        style={{ width: '18px', height: '18px', accentColor: '#8b5cf6' }}
                    />
                    <div>
                        <span style={{ fontSize: '14px', fontWeight: '500' }}>Gift Splitting (Married)</span>
                        <p style={{ fontSize: '11px', opacity: 0.6, margin: '4px 0 0 0' }}>
                            Doubles annual exclusion to {formatCurrency(results.annualExclusion * 2)}
                        </p>
                    </div>
                </label>
            </div>

            <div className="input-group">
                <label className="input-label">
                    <Calendar size={14} style={{ marginRight: '6px' }} />
                    Previous Taxable Lifetime Gifts
                </label>
                <input
                    type="number"
                    value={previousGifts}
                    onChange={(e) => setPreviousGifts(Number(e.target.value))}
                    min="0"
                    step="100000"
                    style={{
                        width: '100%',
                        padding: '14px',
                        background: '#1a1a2e',
                        border: '1px solid #333',
                        borderRadius: '8px',
                        color: 'white',
                        fontSize: '16px'
                    }}
                />
            </div>

            {/* Breakdown */}
            <div style={{
                background: '#1a1a2e',
                borderRadius: '12px',
                padding: '16px',
                marginTop: '16px',
                border: '1px solid #333'
            }}>
                <h4 style={{ fontSize: '13px', fontWeight: '600', marginBottom: '12px' }}>­ƒôè Gift Breakdown</h4>
                <div style={{ display: 'grid', gap: '8px', fontSize: '13px' }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ opacity: 0.7 }}>Per Recipient</span>
                        <span style={{ fontWeight: '600' }}>{formatCurrency(results.perRecipient)}</span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ opacity: 0.7 }}>Annual Exclusion (per recipient)</span>
                        <span style={{ fontWeight: '600', color: '#10b981' }}>{formatCurrency(results.effectiveExclusion)}</span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ opacity: 0.7 }}>Within Exclusion</span>
                        <span style={{ fontWeight: '600', color: '#10b981' }}>{formatCurrency(results.excludedAmount)}</span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between', borderTop: '1px solid #333', paddingTop: '8px' }}>
                        <span>How Much Taxable</span>
                        <span style={{ fontWeight: '600', color: results.totalTaxableGift > 0 ? '#ef4444' : 'white' }}>
                            {formatCurrency(results.totalTaxableGift)}
                        </span>
                    </div>
                </div>
            </div>
        </CalculatorLayout>
    )
}

export default GiftTaxCalculator

import { useState, useMemo } from 'react'
import { Landmark } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function RetirementCalculator() {
    const [currentAge, setCurrentAge] = useState(30)
    const [retirementAge, setRetirementAge] = useState(65)
    const [currentSavings, setCurrentSavings] = useState(50000)
    const [monthlyContribution, setMonthlyContribution] = useState(500)
    const [annualReturn, setAnnualReturn] = useState(7)
    const [inflationRate, setInflationRate] = useState(2.5)

    const results = useMemo(() => {
        const yearsToRetirement = retirementAge - currentAge
        const monthsToRetirement = yearsToRetirement * 12
        const monthlyReturn = annualReturn / 100 / 12
        const realReturn = (1 + annualReturn / 100) / (1 + inflationRate / 100) - 1

        if (yearsToRetirement <= 0) {
            return {
                totalSavings: currentSavings,
                totalContributions: 0,
                interestEarned: 0,
                inflationAdjusted: currentSavings,
                monthlyRetirementIncome: 0
            }
        }

        // Future value with monthly compounding
        let balance = currentSavings
        const totalContributed = monthlyContribution * monthsToRetirement

        for (let i = 0; i < monthsToRetirement; i++) {
            balance = balance * (1 + monthlyReturn) + monthlyContribution
        }

        const interestEarned = balance - currentSavings - totalContributed

        // Inflation adjusted value
        const inflationAdjusted = balance / Math.pow(1 + inflationRate / 100, yearsToRetirement)

        // Estimated monthly income (4% rule, over 25 years)
        const monthlyRetirementIncome = inflationAdjusted * 0.04 / 12

        return {
            totalSavings: balance,
            totalContributions: totalContributed,
            interestEarned,
            inflationAdjusted,
            monthlyRetirementIncome
        }
    }, [currentAge, retirementAge, currentSavings, monthlyContribution, annualReturn, inflationRate])

    const formatCurrency = (value) => {
        return new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
            minimumFractionDigits: 0,
            maximumFractionDigits: 0
        }).format(value)
    }

    return (
        <CalculatorLayout
            title="Retirement Calculator"
            description="Plan your retirement savings and estimate future income"
            category="Finance"
            categoryPath="/finance"
            icon={Landmark}
            result={formatCurrency(results.totalSavings)}
            resultLabel="Retirement Savings"
        >
            <div className="input-row">
                <div className="input-group">
                    <label className="input-label">Current Age</label>
                    <input
                        type="number"
                        value={currentAge}
                        onChange={(e) => setCurrentAge(Number(e.target.value))}
                        min={18}
                        max={100}
                    />
                </div>
                <div className="input-group">
                    <label className="input-label">Retirement Age</label>
                    <input
                        type="number"
                        value={retirementAge}
                        onChange={(e) => setRetirementAge(Number(e.target.value))}
                        min={currentAge}
                        max={100}
                    />
                </div>
            </div>

            <div className="input-group">
                <label className="input-label">Current Savings</label>
                <input
                    type="number"
                    value={currentSavings}
                    onChange={(e) => setCurrentSavings(Number(e.target.value))}
                    min={0}
                />
            </div>

            <div className="input-group">
                <label className="input-label">Monthly Contribution</label>
                <input
                    type="number"
                    value={monthlyContribution}
                    onChange={(e) => setMonthlyContribution(Number(e.target.value))}
                    min={0}
                />
            </div>

            <div className="input-row">
                <div className="input-group">
                    <label className="input-label">Annual Return (%)</label>
                    <input
                        type="number"
                        value={annualReturn}
                        onChange={(e) => setAnnualReturn(Number(e.target.value))}
                        min={0}
                        max={20}
                        step={0.1}
                    />
                </div>
                <div className="input-group">
                    <label className="input-label">Inflation Rate (%)</label>
                    <input
                        type="number"
                        value={inflationRate}
                        onChange={(e) => setInflationRate(Number(e.target.value))}
                        min={0}
                        max={10}
                        step={0.1}
                    />
                </div>
            </div>

            <div className="result-details">
                <div className="result-detail-row">
                    <span className="result-detail-label">Total Contributions</span>
                    <span className="result-detail-value">{formatCurrency(results.totalContributions)}</span>
                </div>
                <div className="result-detail-row">
                    <span className="result-detail-label">Interest Earned</span>
                    <span className="result-detail-value">{formatCurrency(results.interestEarned)}</span>
                </div>
                <div className="result-detail-row">
                    <span className="result-detail-label">Inflation Adjusted</span>
                    <span className="result-detail-value">{formatCurrency(results.inflationAdjusted)}</span>
                </div>
                <div className="result-detail-row">
                    <span className="result-detail-label">Monthly Income (4% Rule)</span>
                    <span className="result-detail-value">{formatCurrency(results.monthlyRetirementIncome)}</span>
                </div>
            </div>
        </CalculatorLayout>
    )
}

export default RetirementCalculator

import { useState, useMemo } from 'react'
import { CreditCard, Plus, Trash2, DollarSign, Calendar, AlertTriangle } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function SubscriptionCostCalculator() {
    const [subscriptions, setSubscriptions] = useState([
        { name: 'Netflix', cost: 15.99, frequency: 'monthly' },
        { name: 'Spotify', cost: 10.99, frequency: 'monthly' },
        { name: 'Amazon Prime', cost: 139, frequency: 'yearly' },
        { name: 'Disney+', cost: 7.99, frequency: 'monthly' },
        { name: 'iCloud', cost: 2.99, frequency: 'monthly' }
    ])

    const addSubscription = () => {
        setSubscriptions([...subscriptions, { name: '', cost: 0, frequency: 'monthly' }])
    }

    const updateSubscription = (index, field, value) => {
        const updated = [...subscriptions]
        updated[index][field] = field === 'cost' ? Number(value) : value
        setSubscriptions(updated)
    }

    const removeSubscription = (index) => {
        setSubscriptions(subscriptions.filter((_, i) => i !== index))
    }

    const results = useMemo(() => {
        let monthlyTotal = 0

        const subsWithMonthly = subscriptions.map(sub => {
            let monthly = 0
            switch (sub.frequency) {
                case 'weekly': monthly = sub.cost * 4.33; break
                case 'monthly': monthly = sub.cost; break
                case 'quarterly': monthly = sub.cost / 3; break
                case 'yearly': monthly = sub.cost / 12; break
                default: monthly = sub.cost
            }
            monthlyTotal += monthly
            return { ...sub, monthly }
        })

        // Sort by monthly cost descending
        const sorted = [...subsWithMonthly].sort((a, b) => b.monthly - a.monthly)

        return {
            subscriptions: subsWithMonthly,
            sorted,
            monthlyTotal,
            yearlyTotal: monthlyTotal * 12,
            dailyCost: monthlyTotal / 30,
            weeklyTotal: monthlyTotal / 4.33,
            fiveYearCost: monthlyTotal * 12 * 5,
            tenYearCost: monthlyTotal * 12 * 10
        }
    }, [subscriptions])

    const formatCurrency = (value) => {
        return new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
            minimumFractionDigits: 2,
            maximumFractionDigits: 2
        }).format(value)
    }

    const handleReset = () => {
        setSubscriptions([
            { name: 'Netflix', cost: 15.99, frequency: 'monthly' },
            { name: 'Spotify', cost: 10.99, frequency: 'monthly' },
            { name: 'Amazon Prime', cost: 139, frequency: 'yearly' },
            { name: 'Disney+', cost: 7.99, frequency: 'monthly' },
            { name: 'iCloud', cost: 2.99, frequency: 'monthly' }
        ])
    }

    return (
        <CalculatorLayout
            title="Subscription Cost Calculator"
            description="Track all your subscriptions and see the true cost"
            category="Finance"
            categoryPath="/finance"
            icon={CreditCard}
            result={formatCurrency(results.monthlyTotal) + "/mo"}
            resultLabel="Monthly Total"
            onReset={handleReset}
        >
            {/* Summary Cards */}
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(2, 1fr)',
                gap: '12px',
                marginBottom: '24px'
            }}>
                <div style={{
                    background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                    borderRadius: '12px',
                    padding: '16px',
                    textAlign: 'center'
                }}>
                    <div style={{ fontSize: '12px', opacity: 0.9, marginBottom: '4px' }}>­ƒÆ© Monthly</div>
                    <div style={{ fontSize: '22px', fontWeight: '700' }}>{formatCurrency(results.monthlyTotal)}</div>
                </div>

                <div style={{
                    background: 'linear-gradient(135deg, #f97316 0%, #ea580c 100%)',
                    borderRadius: '12px',
                    padding: '16px',
                    textAlign: 'center'
                }}>
                    <div style={{ fontSize: '12px', opacity: 0.9, marginBottom: '4px' }}>­ƒôà Yearly</div>
                    <div style={{ fontSize: '22px', fontWeight: '700' }}>{formatCurrency(results.yearlyTotal)}</div>
                </div>
            </div>

            {/* Subscriptions List */}
            <div style={{ marginBottom: '16px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '12px' }}>
                    <h3 style={{ fontSize: '14px', fontWeight: '600' }}>
                        Your Subscriptions ({subscriptions.length})
                    </h3>
                    <button
                        onClick={addSubscription}
                        style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '4px',
                            padding: '8px 16px',
                            background: '#8b5cf6',
                            border: 'none',
                            borderRadius: '6px',
                            color: 'white',
                            fontSize: '12px',
                            cursor: 'pointer'
                        }}
                    >
                        <Plus size={14} /> Add
                    </button>
                </div>

                {subscriptions.map((sub, index) => (
                    <div key={index} style={{
                        background: '#1a1a2e',
                        borderRadius: '10px',
                        padding: '12px',
                        marginBottom: '8px',
                        border: '1px solid #333',
                        display: 'flex',
                        gap: '8px',
                        alignItems: 'center',
                        flexWrap: 'wrap'
                    }}>
                        <input
                            type="text"
                            value={sub.name}
                            onChange={(e) => updateSubscription(index, 'name', e.target.value)}
                            placeholder="Service name"
                            style={{
                                flex: '1',
                                minWidth: '100px',
                                padding: '10px',
                                background: '#0a0a0a',
                                border: '1px solid #333',
                                borderRadius: '6px',
                                color: 'white',
                                fontSize: '14px'
                            }}
                        />
                        <div style={{ display: 'flex', alignItems: 'center', gap: '4px' }}>
                            <span style={{ opacity: 0.5 }}>$</span>
                            <input
                                type="number"
                                value={sub.cost}
                                onChange={(e) => updateSubscription(index, 'cost', e.target.value)}
                                step="0.01"
                                min="0"
                                style={{
                                    width: '70px',
                                    padding: '10px',
                                    background: '#0a0a0a',
                                    border: '1px solid #333',
                                    borderRadius: '6px',
                                    color: 'white',
                                    fontSize: '14px'
                                }}
                            />
                        </div>
                        <select
                            value={sub.frequency}
                            onChange={(e) => updateSubscription(index, 'frequency', e.target.value)}
                            style={{
                                padding: '10px',
                                background: '#0a0a0a',
                                border: '1px solid #333',
                                borderRadius: '6px',
                                color: 'white',
                                fontSize: '13px'
                            }}
                        >
                            <option value="weekly">Weekly</option>
                            <option value="monthly">Monthly</option>
                            <option value="quarterly">Quarterly</option>
                            <option value="yearly">Yearly</option>
                        </select>
                        <button
                            onClick={() => removeSubscription(index)}
                            style={{
                                padding: '8px',
                                background: '#ef444420',
                                border: 'none',
                                borderRadius: '6px',
                                color: '#ef4444',
                                cursor: 'pointer'
                            }}
                        >
                            <Trash2 size={16} />
                        </button>
                    </div>
                ))}
            </div>

            {/* Cost Breakdown */}
            <div style={{
                background: '#1a1a2e',
                borderRadius: '12px',
                padding: '16px',
                border: '1px solid #333'
            }}>
                <h4 style={{ fontSize: '13px', fontWeight: '600', marginBottom: '12px' }}>­ƒôè Cost Breakdown</h4>
                <div style={{ display: 'grid', gap: '8px', fontSize: '13px' }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ opacity: 0.7 }}>Daily Cost</span>
                        <span style={{ fontWeight: '600' }}>{formatCurrency(results.dailyCost)}</span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ opacity: 0.7 }}>Weekly Cost</span>
                        <span style={{ fontWeight: '600' }}>{formatCurrency(results.weeklyTotal)}</span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between', borderTop: '1px solid #333', paddingTop: '8px' }}>
                        <span style={{ opacity: 0.7 }}>5-Year Total</span>
                        <span style={{ fontWeight: '600', color: '#f97316' }}>{formatCurrency(results.fiveYearCost)}</span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <span style={{ opacity: 0.7 }}>10-Year Total</span>
                        <span style={{ fontWeight: '600', color: '#ef4444' }}>{formatCurrency(results.tenYearCost)}</span>
                    </div>
                </div>
            </div>

            {/* Top Expenses */}
            {results.sorted.length > 0 && (
                <div style={{
                    background: 'rgba(239, 68, 68, 0.1)',
                    borderRadius: '12px',
                    padding: '16px',
                    marginTop: '16px',
                    border: '1px solid rgba(239, 68, 68, 0.2)'
                }}>
                    <h4 style={{ fontSize: '13px', fontWeight: '600', marginBottom: '8px' }}>
                        <AlertTriangle size={14} style={{ marginRight: '6px' }} />
                        Biggest Expenses
                    </h4>
                    <div style={{ fontSize: '12px', lineHeight: '1.8' }}>
                        {results.sorted.slice(0, 3).map((sub, i) => (
                            <div key={i} style={{ display: 'flex', justifyContent: 'space-between' }}>
                                <span>{sub.name || 'Unnamed'}</span>
                                <span style={{ color: '#ef4444', fontWeight: '600' }}>
                                    {formatCurrency(sub.monthly)}/mo
                                </span>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </CalculatorLayout>
    )
}

