import { Grid3x3 } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function MatrixCalculator() {
    const [matrixA, setMatrixA] = useState([[1, 2], [3, 4]])
    const [matrixB, setMatrixB] = useState([[5, 6], [7, 8]])
    const [operation, setOperation] = useState('add')

    const updateCell = (matrix, setMatrix, row, col, value) => {
        const newMatrix = matrix.map((r, i) =>
            r.map((c, j) => (i === row && j === col) ? Number(value) || 0 : c)
        )
        setMatrix(newMatrix)
    }

    const results = useMemo(() => {
        const a = matrixA
        const b = matrixB
        let result = []

        try {
            if (operation === 'add') {
                result = a.map((row, i) => row.map((val, j) => val + b[i][j]))
            } else if (operation === 'subtract') {
                result = a.map((row, i) => row.map((val, j) => val - b[i][j]))
            } else if (operation === 'multiply') {
                // Matrix multiplication
                result = a.map((row, i) =>
                    b[0].map((_, j) =>
                        row.reduce((sum, val, k) => sum + val * b[k][j], 0)
                    )
                )
            } else if (operation === 'determinant') {
                const det = a[0][0] * a[1][1] - a[0][1] * a[1][0]
                return { determinant: det }
            } else if (operation === 'transpose') {
                result = a[0].map((_, i) => a.map(row => row[i]))
            }
            return { matrix: result }
        } catch {
            return { error: 'Invalid operation' }
        }
    }, [matrixA, matrixB, operation])

    const renderMatrix = (matrix, setMatrix, label) => (
        <div style={{ marginBottom: '16px' }}>
            <label className="input-label">{label}</label>
            <div style={{ display: 'grid', gridTemplateColumns: `repeat(${matrix[0].length}, 60px)`, gap: '4px' }}>
                {matrix.map((row, i) =>
                    row.map((cell, j) => (
                        <input
                            key={`${i}-${j}`}
                            type="number"
                            value={cell}
                            onChange={(e) => updateCell(matrix, setMatrix, i, j, e.target.value)}
                            style={{ width: '60px', textAlign: 'center', padding: '8px' }}
                        />
                    ))
                )}
            </div>
        </div>
    )

    return (
        <CalculatorLayout
            title="Matrix Calculator"
            description="Add, subtract, multiply matrices"
            category="Math"
            categoryPath="/math"
            icon={Grid3x3}
            result={results.determinant !== undefined ? results.determinant : 'Calculated'}
            resultLabel={results.determinant !== undefined ? 'Determinant' : 'Result'}
        >
            <div className="input-group">
                <label className="input-label">Operation</label>
                <select value={operation} onChange={(e) => setOperation(e.target.value)}>
                    <option value="add">Add (A + B)</option>
                    <option value="subtract">Subtract (A - B)</option>
                    <option value="multiply">Multiply (A ├ù B)</option>
                    <option value="determinant">Determinant (A)</option>
                    <option value="transpose">Transpose (A)</option>
                </select>
            </div>
            {renderMatrix(matrixA, setMatrixA, 'Matrix A (2├ù2)')}
            {!['determinant', 'transpose'].includes(operation) && renderMatrix(matrixB, setMatrixB, 'Matrix B (2├ù2)')}

            {results.matrix && (
                <div className="result-details">
                    <div style={{ fontSize: '13px', opacity: 0.7, marginBottom: '8px' }}>Result:</div>
                    <div style={{ display: 'grid', gridTemplateColumns: `repeat(${results.matrix[0].length}, 60px)`, gap: '4px' }}>
                        {results.matrix.map((row, i) =>
                            row.map((cell, j) => (
                                <div key={`${i}-${j}`} style={{
                                    background: '#333',
                                    padding: '8px',
                                    borderRadius: '4px',
                                    textAlign: 'center',
                                    fontFamily: 'monospace'
                                }}>
                                    {cell}
                                </div>
                            ))
                        )}
                    </div>
                </div>
            )}
        </CalculatorLayout>
    )
}

export default MatrixCalculator

import { useState, useMemo } from 'react'
import { Hash } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function RomanNumeralConverter() {
    const [mode, setMode] = useState('toRoman')
    const [arabicNumber, setArabicNumber] = useState(2024)
    const [romanInput, setRomanInput] = useState('MMXXIV')

    const romanNumerals = [
        { value: 1000, numeral: 'M' },
        { value: 900, numeral: 'CM' },
        { value: 500, numeral: 'D' },
        { value: 400, numeral: 'CD' },
        { value: 100, numeral: 'C' },
        { value: 90, numeral: 'XC' },
        { value: 50, numeral: 'L' },
        { value: 40, numeral: 'XL' },
        { value: 10, numeral: 'X' },
        { value: 9, numeral: 'IX' },
        { value: 5, numeral: 'V' },
        { value: 4, numeral: 'IV' },
        { value: 1, numeral: 'I' }
    ]

    const results = useMemo(() => {
        if (mode === 'toRoman') {
            let num = arabicNumber
            let result = ''
            for (const { value, numeral } of romanNumerals) {
                while (num >= value) {
                    result += numeral
                    num -= value
                }
            }
            return { output: result || 'N/A', valid: arabicNumber > 0 && arabicNumber <= 3999 }
        } else {
            const romanMap = { I: 1, V: 5, X: 10, L: 50, C: 100, D: 500, M: 1000 }
            let result = 0
            const upper = romanInput.toUpperCase()
            for (let i = 0; i < upper.length; i++) {
                const curr = romanMap[upper[i]]
                const next = romanMap[upper[i + 1]]
                if (!curr) return { output: 'Invalid', valid: false }
                if (next && curr < next) {
                    result -= curr
                } else {
                    result += curr
                }
            }
            return { output: result.toString(), valid: result > 0 }
        }
    }, [mode, arabicNumber, romanInput])

    return (
        <CalculatorLayout
            title="Roman Numeral Converter"
            description="Convert between Arabic and Roman numerals"
            category="Math"
            categoryPath="/math"
            icon={Hash}
            result={results.output}
            resultLabel={mode === 'toRoman' ? 'Roman Numeral' : 'Arabic Number'}
        >
            <div className="input-group">
                <label className="input-label">Conversion Mode</label>
                <select value={mode} onChange={(e) => setMode(e.target.value)}>
                    <option value="toRoman">Arabic ÔåÆ Roman</option>
                    <option value="toArabic">Roman ÔåÆ Arabic</option>
                </select>
            </div>
            {mode === 'toRoman' ? (
                <div className="input-group">
                    <label className="input-label">Arabic Number (1-3999)</label>
                    <input type="number" value={arabicNumber} onChange={(e) => setArabicNumber(Number(e.target.value))} min={1} max={3999} />
                </div>
            ) : (
                <div className="input-group">
                    <label className="input-label">Roman Numeral</label>
                    <input type="text" value={romanInput} onChange={(e) => setRomanInput(e.target.value.toUpperCase())} placeholder="MMXXIV" />
                </div>
            )}
            <div className="result-details">
                <div className="result-detail-row">
                    <span className="result-detail-label">Result</span>
                    <span className="result-detail-value" style={{ color: results.valid ? 'inherit' : '#ef4444' }}>
                        {results.output}
                    </span>
                </div>
            </div>
            <div style={{ marginTop: '16px', fontSize: '12px', opacity: 0.6 }}>
                I=1, V=5, X=10, L=50, C=100, D=500, M=1000
            </div>
        </CalculatorLayout>
    )
}

export default RomanNumeralConverter

import { useState, useCallback } from 'react'
import { Calculator } from 'lucide-react'
import './ScientificCalculator.css'

function ScientificCalculator() {
    const [display, setDisplay] = useState('0')
    const [expression, setExpression] = useState('')
    const [memory, setMemory] = useState(0)
    const [isRadians, setIsRadians] = useState(true)

    const handleNumber = useCallback((num) => {
        setDisplay(prev => prev === '0' || prev === 'Error' ? num : prev + num)
    }, [])

    const handleOperator = useCallback((op) => {
        setExpression(display + ' ' + op + ' ')
        setDisplay('0')
    }, [display])

    const handleFunction = useCallback((func) => {
        try {
            const num = parseFloat(display)
            let result

            switch (func) {
                case 'sin':
                    result = isRadians ? Math.sin(num) : Math.sin(num * Math.PI / 180)
                    break
                case 'cos':
                    result = isRadians ? Math.cos(num) : Math.cos(num * Math.PI / 180)
                    break
                case 'tan':
                    result = isRadians ? Math.tan(num) : Math.tan(num * Math.PI / 180)
                    break
                case 'sqrt':
                    result = Math.sqrt(num)
                    break
                case 'log':
                    result = Math.log10(num)
                    break
                case 'ln':
                    result = Math.log(num)
                    break
                case 'x┬▓':
                    result = Math.pow(num, 2)
                    break
                case 'x┬│':
                    result = Math.pow(num, 3)
                    break
                case '1/x':
                    result = 1 / num
                    break
                case '┬▒':
                    result = -num
                    break
                case '%':
                    result = num / 100
                    break
                case '¤Ç':
                    result = Math.PI
                    break
                case 'e':
                    result = Math.E
                    break
                case '!':
                    result = factorial(num)
                    break
                default:
                    result = num
            }

            setDisplay(String(result))
        } catch (e) {
            setDisplay('Error')
        }
    }, [display, isRadians])

    const factorial = (n) => {
        if (n < 0) return NaN
        if (n === 0 || n === 1) return 1
        let result = 1
        for (let i = 2; i <= n; i++) result *= i
        return result
    }

    const calculate = useCallback(() => {
        try {
            const fullExpression = expression + display
            // Safe evaluation using Function constructor
            const sanitized = fullExpression
                .replace(/├ù/g, '*')
                .replace(/├À/g, '/')
                .replace(/\^/g, '**')
            const result = new Function('return ' + sanitized)()
            setDisplay(String(result))
            setExpression('')
        } catch (e) {
            setDisplay('Error')
        }
    }, [expression, display])

    const clear = useCallback(() => {
        setDisplay('0')
        setExpression('')
    }, [])

    const clearEntry = useCallback(() => {
        setDisplay('0')
    }, [])

    const backspace = useCallback(() => {
        setDisplay(prev => prev.length > 1 ? prev.slice(0, -1) : '0')
    }, [])

    const buttons = [
        ['MC', 'MR', 'M+', 'M-', 'MS'],
        ['2nd', '¤Ç', 'e', 'C', 'Ôî½'],
        ['x┬▓', '1/x', '|x|', 'exp', '%'],
        ['ÔêÜ', '(', ')', 'n!', '├À'],
        ['x╩©', '7', '8', '9', '├ù'],
        ['log', '4', '5', '6', '-'],
        ['ln', '1', '2', '3', '+'],
        ['sin', 'cos', 'tan', '0', '='],
    ]

    const handleButtonClick = (btn) => {
        if (btn >= '0' && btn <= '9') {
            handleNumber(btn)
        } else if (btn === '.') {
            if (!display.includes('.')) handleNumber('.')
        } else if (['+', '-', '├ù', '├À'].includes(btn)) {
            handleOperator(btn)
        } else if (btn === '=') {
            calculate()
        } else if (btn === 'C') {
            clear()
        } else if (btn === 'CE') {
            clearEntry()
        } else if (btn === 'Ôî½') {
            backspace()
        } else if (btn === 'ÔêÜ') {
            handleFunction('sqrt')
        } else if (btn === 'x┬▓') {
            handleFunction('x┬▓')
        } else if (btn === 'x┬│') {
            handleFunction('x┬│')
        } else if (btn === '1/x') {
            handleFunction('1/x')
        } else if (btn === 'sin') {
            handleFunction('sin')
        } else if (btn === 'cos') {
            handleFunction('cos')
        } else if (btn === 'tan') {
            handleFunction('tan')
        } else if (btn === 'log') {
            handleFunction('log')
        } else if (btn === 'ln') {
            handleFunction('ln')
        } else if (btn === '¤Ç') {
            handleFunction('¤Ç')
        } else if (btn === 'e') {
            handleFunction('e')
        } else if (btn === 'n!') {
            handleFunction('!')
        } else if (btn === '%') {
            handleFunction('%')
        } else if (btn === '┬▒') {
            handleFunction('┬▒')
        } else if (btn === 'x╩©') {
            handleOperator('^')
        } else if (btn === '(' || btn === ')') {
            setDisplay(prev => prev === '0' ? btn : prev + btn)
        } else if (btn === 'MC') {
            setMemory(0)
        } else if (btn === 'MR') {
            setDisplay(String(memory))
        } else if (btn === 'M+') {
            setMemory(prev => prev + parseFloat(display))
        } else if (btn === 'M-') {
            setMemory(prev => prev - parseFloat(display))
        } else if (btn === 'MS') {
            setMemory(parseFloat(display))
        } else if (btn === '2nd') {
            setIsRadians(prev => !prev)
        } else if (btn === '|x|') {
            setDisplay(prev => String(Math.abs(parseFloat(prev))))
        } else if (btn === 'exp') {
            setDisplay(prev => String(Math.exp(parseFloat(prev))))
        }
    }

    const getButtonClass = (btn) => {
        if (btn === '=') return 'calc-btn btn-equals'
        if (['+', '-', '├ù', '├À', 'x╩©'].includes(btn)) return 'calc-btn btn-operator'
        if (['sin', 'cos', 'tan', 'log', 'ln', 'ÔêÜ', 'x┬▓', 'x┬│', 'n!', '%', '1/x', '|x|', 'exp'].includes(btn)) return 'calc-btn btn-function'
        if (['MC', 'MR', 'M+', 'M-', 'MS'].includes(btn)) return 'calc-btn btn-memory'
        if (['C', 'Ôî½', '2nd'].includes(btn)) return 'calc-btn btn-clear'
        if (['¤Ç', 'e', '(', ')'].includes(btn)) return 'calc-btn btn-constant'
        return 'calc-btn btn-number'
    }

    return (
        <div className="scientific-calculator">
            <div className="calc-container">
                <div className="calc-header">
                    <h1 className="calc-title">Scientific Calculator</h1>
                    <div className="calc-mode">
                        <span className={isRadians ? 'active' : ''}>RAD</span>
                        <span className={!isRadians ? 'active' : ''}>DEG</span>
                    </div>
                </div>

                <div className="calc-display">
                    <div className="display-expression">{expression}</div>
                    <div className="display-result">{display}</div>
                    {memory !== 0 && <div className="display-memory">M: {memory}</div>}
                </div>

                <div className="calc-keypad">
                    {buttons.map((row, rowIndex) => (
                        <div key={rowIndex} className="calc-row">
                            {row.map((btn) => (
                                <button
                                    key={btn}
                                    className={getButtonClass(btn)}
                                    onClick={() => handleButtonClick(btn)}
                                >
                                    {btn}
                                </button>
                            ))}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    )
}

export default ScientificCalculator

import { useState, useEffect } from 'react'
import { Timer } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function CountdownTimer() {
    const [hours, setHours] = useState(0)
    const [minutes, setMinutes] = useState(5)
    const [seconds, setSeconds] = useState(0)
    const [timeLeft, setTimeLeft] = useState(0)
    const [isRunning, setIsRunning] = useState(false)

    useEffect(() => {
        let interval
        if (isRunning && timeLeft > 0) {
            interval = setInterval(() => {
                setTimeLeft(prev => prev - 1)
            }, 1000)
        } else if (timeLeft === 0 && isRunning) {
            setIsRunning(false)
            // Simple alert when timer completes
            if (Notification.permission === 'granted') {
                new Notification('Timer Complete!')
            }
        }
        return () => clearInterval(interval)
    }, [isRunning, timeLeft])

    const startTimer = () => {
        const totalSeconds = hours * 3600 + minutes * 60 + seconds
        setTimeLeft(totalSeconds)
        setIsRunning(true)
    }

    const pauseTimer = () => setIsRunning(false)
    const resumeTimer = () => setIsRunning(true)
    const resetTimer = () => {
        setIsRunning(false)
        setTimeLeft(0)
    }

    const formatTime = (secs) => {
        const h = Math.floor(secs / 3600)
        const m = Math.floor((secs % 3600) / 60)
        const s = secs % 60
        return `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`
    }

    const presets = [
        { label: '1 min', value: 60 },
        { label: '5 min', value: 300 },
        { label: '10 min', value: 600 },
        { label: '25 min', value: 1500 },
    ]

    return (
        <CalculatorLayout
            title="Countdown Timer"
            description="Set countdowns with notifications"
            category="Other"
            categoryPath="/other"
            icon={Timer}
            result={formatTime(timeLeft)}
            resultLabel={isRunning ? 'Running' : 'Timer'}
        >
            {!isRunning && timeLeft === 0 && (
                <>
                    <div className="input-row">
                        <div className="input-group">
                            <label className="input-label">Hours</label>
                            <input type="number" value={hours} onChange={(e) => setHours(Number(e.target.value))} min={0} max={23} />
                        </div>
                        <div className="input-group">
                            <label className="input-label">Minutes</label>
                            <input type="number" value={minutes} onChange={(e) => setMinutes(Number(e.target.value))} min={0} max={59} />
                        </div>
                        <div className="input-group">
                            <label className="input-label">Seconds</label>
                            <input type="number" value={seconds} onChange={(e) => setSeconds(Number(e.target.value))} min={0} max={59} />
                        </div>
                    </div>
                    <div style={{ display: 'flex', gap: '8px', marginBottom: '16px' }}>
                        {presets.map(p => (
                            <button key={p.label} onClick={() => {
                                setTimeLeft(p.value)
                                setIsRunning(true)
                            }} style={{
                                flex: 1, padding: '10px', background: '#333', border: 'none',
                                borderRadius: '6px', color: 'white', cursor: 'pointer', fontSize: '12px'
                            }}>
                                {p.label}
                            </button>
                        ))}
                    </div>
                </>
            )}

            <div style={{
                fontSize: '48px',
                fontFamily: 'monospace',
                textAlign: 'center',
                margin: '24px 0',
                color: timeLeft < 10 && timeLeft > 0 ? '#ef4444' : 'inherit'
            }}>
                {formatTime(timeLeft)}
            </div>

            <div style={{ display: 'flex', gap: '8px' }}>
                {!isRunning && timeLeft === 0 && (
                    <button onClick={startTimer} style={{
                        flex: 1, padding: '12px', background: '#10b981', border: 'none',
                        borderRadius: '8px', color: 'white', cursor: 'pointer'
                    }}>Start</button>
                )}
                {isRunning && (
                    <button onClick={pauseTimer} style={{
                        flex: 1, padding: '12px', background: '#f59e0b', border: 'none',
                        borderRadius: '8px', color: 'white', cursor: 'pointer'
                    }}>Pause</button>
                )}
                {!isRunning && timeLeft > 0 && (
                    <button onClick={resumeTimer} style={{
                        flex: 1, padding: '12px', background: '#10b981', border: 'none',
                        borderRadius: '8px', color: 'white', cursor: 'pointer'
                    }}>Resume</button>
                )}
                {timeLeft > 0 && (
                    <button onClick={resetTimer} style={{
                        flex: 1, padding: '12px', background: '#ef4444', border: 'none',
                        borderRadius: '8px', color: 'white', cursor: 'pointer'
                    }}>Reset</button>
                )}
            </div>
        </CalculatorLayout>
    )
}

export default CountdownTimer

import { useState, useMemo } from 'react'
import { Globe } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function TimezoneConverter() {
    const [time, setTime] = useState('12:00')
    const [fromZone, setFromZone] = useState('America/New_York')
    const [toZone, setToZone] = useState('Asia/Kolkata')

    const timezones = [
        { value: 'America/New_York', label: 'New York (EST/EDT)', offset: -5 },
        { value: 'America/Los_Angeles', label: 'Los Angeles (PST/PDT)', offset: -8 },
        { value: 'America/Chicago', label: 'Chicago (CST/CDT)', offset: -6 },
        { value: 'Europe/London', label: 'London (GMT/BST)', offset: 0 },
        { value: 'Europe/Paris', label: 'Paris (CET/CEST)', offset: 1 },
        { value: 'Europe/Berlin', label: 'Berlin (CET/CEST)', offset: 1 },
        { value: 'Asia/Tokyo', label: 'Tokyo (JST)', offset: 9 },
        { value: 'Asia/Shanghai', label: 'Shanghai (CST)', offset: 8 },
        { value: 'Asia/Kolkata', label: 'India (IST)', offset: 5.5 },
        { value: 'Asia/Dubai', label: 'Dubai (GST)', offset: 4 },
        { value: 'Australia/Sydney', label: 'Sydney (AEST/AEDT)', offset: 11 },
        { value: 'Pacific/Auckland', label: 'Auckland (NZST/NZDT)', offset: 13 },
    ]

    const results = useMemo(() => {
        try {
            const [hours, minutes] = time.split(':').map(Number)

            // Get timezone offsets
            const fromTz = timezones.find(tz => tz.value === fromZone)
            const toTz = timezones.find(tz => tz.value === toZone)

            if (!fromTz || !toTz) return { fromTime: '--:--', toTime: '--:--', nextDay: false, prevDay: false }

            // Calculate the difference in hours
            const offsetDiff = toTz.offset - fromTz.offset

            // Convert to minutes for easier calculation
            let totalMinutes = hours * 60 + minutes
            const offsetMinutes = offsetDiff * 60

            // Apply the offset
            let convertedMinutes = totalMinutes + offsetMinutes

            // Handle day overflow
            let nextDay = false
            let prevDay = false

            if (convertedMinutes >= 24 * 60) {
                convertedMinutes -= 24 * 60
                nextDay = true
            } else if (convertedMinutes < 0) {
                convertedMinutes += 24 * 60
                prevDay = true
            }

            // Convert back to hours and minutes
            const convertedHours = Math.floor(convertedMinutes / 60)
            const convertedMins = Math.round(convertedMinutes % 60)

            // Format times
            const formatTime = (h, m) => {
                const hour12 = h % 12 || 12
                const ampm = h >= 12 ? 'PM' : 'AM'
                return `${hour12}:${m.toString().padStart(2, '0')} ${ampm}`
            }

            const fromTime = formatTime(hours, minutes)
            const toTime = formatTime(convertedHours, convertedMins)

            // Calculate time difference
            const timeDiff = offsetDiff >= 0 ? `+${offsetDiff}` : `${offsetDiff}`

            return { fromTime, toTime, nextDay, prevDay, timeDiff }
        } catch {
            return { fromTime: '--:--', toTime: '--:--', nextDay: false, prevDay: false, timeDiff: '0' }
        }
    }, [time, fromZone, toZone])

    return (
        <CalculatorLayout
            title="Timezone Converter"
            description="Convert time between timezones"
            category="Other"
            categoryPath="/calculators?category=Other"
            icon={Globe}
            result={results.toTime}
            resultLabel={results.nextDay ? '(Next Day)' : results.prevDay ? '(Previous Day)' : 'Converted Time'}
        >
            <div className="input-group">
                <label className="input-label">Time</label>
                <input type="time" value={time} onChange={(e) => setTime(e.target.value)} />
            </div>
            <div className="input-group">
                <label className="input-label">From Timezone</label>
                <select value={fromZone} onChange={(e) => setFromZone(e.target.value)}>
                    {timezones.map(tz => (
                        <option key={tz.value} value={tz.value}>{tz.label}</option>
                    ))}
                </select>
            </div>
            <div className="input-group">
                <label className="input-label">To Timezone</label>
                <select value={toZone} onChange={(e) => setToZone(e.target.value)}>
                    {timezones.map(tz => (
                        <option key={tz.value} value={tz.value}>{tz.label}</option>
                    ))}
                </select>
            </div>
            <div className="result-details">
                <div className="result-detail-row">
                    <span className="result-detail-label">Original Time</span>
                    <span className="result-detail-value">{results.fromTime}</span>
                </div>
