import { useState, useRef } from 'react'
import { QrCode } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function QRCodeGenerator() {
    const [text, setText] = useState('https://plainly.live')
    const [size, setSize] = useState(200)

    const qrUrl = `https://api.qrserver.com/v1/create-qr-code/?size=${size}x${size}&data=${encodeURIComponent(text)}`

    const handleDownload = async () => {
        try {
            const response = await fetch(qrUrl)
            const blob = await response.blob()
            const url = URL.createObjectURL(blob)
            const a = document.createElement('a')
            a.href = url
            a.download = 'qrcode.png'
            a.click()
            URL.revokeObjectURL(url)
        } catch (e) {
            console.error('Download failed', e)
        }
    }

    return (
        <CalculatorLayout
            title="QR Code Generator"
            description="Generate QR codes for URLs, text, and more"
            category="Tech"
            categoryPath="/tech"
            icon={QrCode}
            result="Generated"
            resultLabel="QR Code"
        >
            <div className="input-group">
                <label className="input-label">Text or URL</label>
                <input
                    type="text"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Enter text or URL"
                />
            </div>
            <div className="input-group">
                <label className="input-label">Size (px)</label>
                <select value={size} onChange={(e) => setSize(Number(e.target.value))}>
                    <option value={150}>Small (150px)</option>
                    <option value={200}>Medium (200px)</option>
                    <option value={300}>Large (300px)</option>
                    <option value={400}>Extra Large (400px)</option>
                </select>
            </div>
            {text && (
                <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    gap: '16px',
                    marginTop: '16px'
                }}>
                    <img
                        src={qrUrl}
                        alt="QR Code"
                        style={{
                            background: 'white',
                            padding: '16px',
                            borderRadius: '12px',
                            maxWidth: '100%'
                        }}
                    />
                    <button onClick={handleDownload} style={{
                        width: '100%',
                        padding: '12px',
                        background: '#333',
                        border: 'none',
                        borderRadius: '8px',
                        color: 'white',
                        cursor: 'pointer'
                    }}>
                        Download QR Code
                    </button>
                </div>
            )}
        </CalculatorLayout>
    )
}

export default QRCodeGenerator

import { useState, useMemo } from 'react'
import { Link2 } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function URLEncoder() {
    const [mode, setMode] = useState('encode')
    const [input, setInput] = useState('Hello World! How are you?')

    const result = useMemo(() => {
        try {
            if (mode === 'encode') {
                return encodeURIComponent(input)
            } else {
                return decodeURIComponent(input)
            }
        } catch (e) {
            return 'Invalid input'
        }
    }, [mode, input])

    const copyToClipboard = () => {
        navigator.clipboard.writeText(result)
    }

    return (
        <CalculatorLayout
            title="URL Encoder/Decoder"
            description="Encode or decode URL strings"
            category="Tech"
            categoryPath="/calculators?category=Tech"
            icon={Link2}
            result={result.length > 20 ? result.slice(0, 20) + '...' : result}
            resultLabel={mode === 'encode' ? 'Encoded' : 'Decoded'}
        >
            <div className="input-group">
                <label className="input-label">Mode</label>
                <select value={mode} onChange={(e) => setMode(e.target.value)}>
                    <option value="encode">Encode URL</option>
                    <option value="decode">Decode URL</option>
                </select>
            </div>
            <div className="input-group">
                <label className="input-label">Input</label>
                <textarea
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    rows={3}
                    placeholder={mode === 'encode' ? 'Enter text to encode...' : 'Enter URL to decode...'}
                />
            </div>
            <div className="input-group">
                <label className="input-label">Output</label>
                <textarea
                    value={result}
                    readOnly
                    rows={3}
                    style={{ background: '#1a1a2e' }}
                />
            </div>
            <button
                onClick={copyToClipboard}
                style={{
                    width: '100%',
                    padding: '12px',
                    background: '#a78bfa',
                    border: 'none',
                    borderRadius: '8px',
                    color: '#000',
                    fontWeight: 600,
                    cursor: 'pointer'
                }}
            >
                Copy Result
            </button>
        </CalculatorLayout>
    )
}

