import { useState, useMemo } from 'react'
import { Shuffle } from 'lucide-react'
import CalculatorLayout from '../../../components/Calculator/CalculatorLayout'

function TextScrambler() {
    const [text, setText] = useState('The quick brown fox jumps over the lazy dog')
    const [mode, setMode] = useState('words')

    const scrambleWord = (word) => {
        if (word.length <= 3) return word
        const first = word[0]
        const last = word[word.length - 1]
        const middle = word.slice(1, -1).split('')

        for (let i = middle.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [middle[i], middle[j]] = [middle[j], middle[i]]
        }

        return first + middle.join('') + last
    }

    const scrambled = useMemo(() => {
        if (!text) return ''

        if (mode === 'words') {
            // Scramble word order
            const words = text.split(' ')
            for (let i = words.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [words[i], words[j]] = [words[j], words[i]]
            }
            return words.join(' ')
        } else if (mode === 'letters') {
            // Cambridge-style scramble (first and last stay, middle scrambled)
            return text.split(' ').map(word => scrambleWord(word)).join(' ')
        } else if (mode === 'full') {
            // Full scramble
            const chars = text.split('')
            for (let i = chars.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [chars[i], chars[j]] = [chars[j], chars[i]]
            }
            return chars.join('')
        }
        return text
    }, [text, mode])

    const [displayedScramble, setDisplayedScramble] = useState(scrambled)

    const rescramble = () => {
        if (!text) return

        let result
        if (mode === 'words') {
            const words = text.split(' ')
            for (let i = words.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [words[i], words[j]] = [words[j], words[i]]
            }
            result = words.join(' ')
        } else if (mode === 'letters') {
            result = text.split(' ').map(word => scrambleWord(word)).join(' ')
        } else if (mode === 'full') {
            const chars = text.split('')
            for (let i = chars.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [chars[i], chars[j]] = [chars[j], chars[i]]
            }
            result = chars.join('')
        }
        setDisplayedScramble(result)
    }

    const copyResult = () => {
        navigator.clipboard.writeText(displayedScramble)
    }

    return (
        <CalculatorLayout
            title="Text Scrambler"
            description="Scramble words or letters"
            category="Text"
            categoryPath="/calculators?category=Text"
            icon={Shuffle}
            result={displayedScramble.length > 20 ? displayedScramble.substring(0, 20) + '...' : displayedScramble}
            resultLabel="Scrambled"
        >
            <div className="input-group">
                <label className="input-label">Scramble Mode</label>
                <select value={mode} onChange={(e) => { setMode(e.target.value); rescramble(); }}>
                    <option value="words">Shuffle Word Order</option>
                    <option value="letters">Scramble Letters (Cambridge style)</option>
                    <option value="full">Full Character Scramble</option>
                </select>
            </div>
            <div className="input-group">
                <label className="input-label">Original Text</label>
                <textarea
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    rows={3}
                    placeholder="Enter text to scramble..."
                />
            </div>
            <button
                onClick={rescramble}
                style={{
                    width: '100%',
                    padding: '14px',
                    background: '#a78bfa',
                    border: 'none',
                    borderRadius: '8px',
                    color: '#000',
                    fontWeight: 600,
                    cursor: 'pointer',
                    marginBottom: '16px',
                    fontSize: '15px'
                }}
            >
                ­ƒöÇ Scramble Again
            </button>
            <div className="input-group">
                <label className="input-label">Scrambled Text</label>
                <textarea
                    value={displayedScramble}
                    readOnly
                    rows={3}
                    style={{ background: '#1a1a2e' }}
                />
            </div>
            <button
                onClick={copyResult}
                style={{
                    width: '100%',
                    padding: '10px',
                    background: '#333',
                    border: '1px solid #444',
                    borderRadius: '8px',
                    color: '#fff',
                    cursor: 'pointer'
                }}
            >
                ­ƒôï Copy Scrambled Text
            </button>
            {mode === 'letters' && (
                <div style={{
                    marginTop: '12px',
                    padding: '12px',
                    background: '#1a1a2e',
                    borderRadius: '8px',
                    fontSize: '12px',
                    opacity: 0.8
                }}>
                    ­ƒÆí Cambridge-style scrambling keeps the first and last letters in place.
                    Raeding tihs is slitl pssoible!
                </div>
            )}
        </CalculatorLayout>
    )
}

export default TextScrambler

/**
 * Plainly AI - Image & Video Generation Service
 * Powered by Pollinations.AI with automatic model fallback
 * Users see "Plainly AI" branding, models auto-fallback from best to worst
 */

const POLLINATIONS_API_KEY = import.meta.env.VITE_POLLINATIONS_API_KEY

// Image models ranked from best to worst quality
// Based on 2026 benchmarks: Seedream 4.5 #1 ranked, Flux best quality
const IMAGE_MODELS = [
    'seedream-pro',   // #1 ranked, best consistency
    'flux',           // Best quality, recommended
    'gptimage-large', // GPT Image 1.5 - creative
    'seedream',       // Seedream 4.0 - artistic
    'gptimage',       // GPT Image 1 Mini
    'klein-large',    // FLUX.2 Klein 9B
    'klein',          // FLUX.2 Klein 4B - fast
    'turbo',          // SDXL Turbo - fastest
    'zimage',         // Z-Image Turbo
    'nanobanana-pro', // NanoBanana Pro
    'nanobanana',     // NanoBanana - experimental
    'kontext'         // FLUX.1 Kontext - context editing
]

// Video models ranked from best to worst
const VIDEO_MODELS = [
    'wan',           // Wan 2.6 - best quality
    'veo',           // Veo 3.1 - Google video AI
    'seedance-pro',  // Seedance Pro - high quality
    'seedance'       // Seedance Lite - fast
]

/**
 * Generate an image with automatic model fallback
 * Tries best model first, falls back to next if it fails
 */
export async function generateImage(prompt, options = {}) {
    const {
        width = 1024,
        height = 1024,
        seed = Math.floor(Math.random() * 1000000)
    } = options

    const encodedPrompt = encodeURIComponent(prompt)

    // Try each model in order until one succeeds
    for (let i = 0; i < IMAGE_MODELS.length; i++) {
        const model = IMAGE_MODELS[i]

        try {
            let imageUrl = `https://image.pollinations.ai/prompt/${encodedPrompt}?width=${width}&height=${height}&seed=${seed}&nologo=true&model=${model}`

            if (POLLINATIONS_API_KEY) {
                imageUrl += `&key=${POLLINATIONS_API_KEY}`
            }

            const response = await fetch(imageUrl)

            if (!response.ok) {
                console.warn(`Model ${model} failed with ${response.status}, trying next...`)
                continue
            }

            // Check if response is actually an image (not rate limit page)
            const contentType = response.headers.get('content-type')
            if (!contentType || !contentType.startsWith('image/')) {
                console.warn(`Model ${model} returned non-image, trying next...`)
                continue
            }

            // Convert to data URL
            const blob = await response.blob()

            // Check blob size - rate limit images are usually small
            if (blob.size < 10000) {
                console.warn(`Model ${model} returned small image (likely rate limit), trying next...`)
                continue
            }

            return new Promise((resolve, reject) => {
                const reader = new FileReader()
                reader.onloadend = () => resolve(reader.result)
                reader.onerror = reject
                reader.readAsDataURL(blob)
            })
        } catch (error) {
            console.warn(`Model ${model} error: ${error.message}, trying next...`)
            continue
        }
    }

    throw new Error('All models failed. Please try again later.')
}

/**
 * Generate a video with automatic model fallback
 * Video models use the same /image/ endpoint but return video content
 */
export async function generateVideo(prompt, options = {}) {
    const encodedPrompt = encodeURIComponent(prompt)

    // Try each video model in order
    for (let i = 0; i < VIDEO_MODELS.length; i++) {
        const model = VIDEO_MODELS[i]

        try {
            let videoUrl = `https://gen.pollinations.ai/image/${encodedPrompt}?model=${model}`

            if (POLLINATIONS_API_KEY) {
                videoUrl += `&key=${POLLINATIONS_API_KEY}`
            }

            const response = await fetch(videoUrl)

            if (!response.ok) {
                console.warn(`Video model ${model} failed with ${response.status}, trying next...`)
                continue
            }

            // Convert to blob URL for video playback
            const blob = await response.blob()

            // Check if it's actually video content
            if (blob.size < 50000) {
                console.warn(`Video model ${model} returned small file, trying next...`)
                continue
            }

            return URL.createObjectURL(blob)
        } catch (error) {
            console.warn(`Video model ${model} error: ${error.message}, trying next...`)
            continue
        }
    }

    throw new Error('Video generation failed. Please try again later.')
}

/**
 * Check if service is configured
 */
export function isConfigured() {
    return true
}

/**
 * Check if API key exists
 */
export function hasApiKey() {
    return !!POLLINATIONS_API_KEY
}

export default {
    generateImage,
    generateVideo,
    isConfigured,
    hasApiKey,
    IMAGE_MODELS,
    VIDEO_MODELS
}
